import React from "react";

class CalculatorForm extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            response: [],
            error: [],
            message: ""
        }
    };

    handleSubmit = (e) => {
        e.preventDefault();

        let x = e.target['x'].value;
        let y = e.target['y'].value;
        this.calculate(x, y)

    };

    calculate(x, y) {
        if (x === "" || y === "") {
            this.setState({error: "x or y value is empty!"})
        } else {
            if (isNaN(x) || isNaN(y)) {
                this.setState({message: x + y})
            } else {
                this.setState({message: parseInt(x) + parseInt(y)})
            }
        }
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div className="get-name-form">
                        <div className="row">
                            <div className="board">
                                <div className="text">
                                    fill in the fields, press the button and get the result - a number, if the fields
                                    are
                                    filled with numbers, a string-concatenation of characters, if the fields are filled
                                    with
                                    characters
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-6">
                                <input type="text" name="x" placeholder="x"/>
                            </div>
                            <div className="col-sm-6">
                                <div className="board">
                                    <div className="text">
                                        {this.state.message}
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-6">
                                <input type="text" name="y" placeholder="y"/>
                            </div>
                            <div className="col-sm-6">
                                <div className="get-name-button">
                                    <button>Calculate</button>
                                </div>
                            </div>
                            {this.state.error}
                        </div>
                        <div className="row h-50">
                            <div className="col-sm-9">
                            </div>
                            <div className="col-sm-1">
                                <div className="img"/>
                            </div>
                        </div>
                    </div>
                </form>
            </div>
        )
    };
}

export default CalculatorForm
